    <script>
        // Update the cart item count
        function updateCartItemCount(count) {
            const cartCount = document.getElementById('cart-item-count');
            // console.log(count);
            cartCount.textContent = count;
        }

        // Call this function to fetch the updated cart item count from the server (via AJAX or page reload)
        function fetchCartItemCount() {
            fetch("{{ route('cart.items.count') }}")
                .then(response => response.json())
                .then(data => {
                    updateCartItemCount(data.count);
                })
                .catch(error => console.error('Error:', error));
        }

        // Fetch the cart count initially when the page loads
        window.onload = fetchCartItemCount;
    </script>


